import React, {useState} from "react";
import Tiles from "./components/tiles";
import './App.css';

function App(props) {

  const [score, setScore] = useState(Math.floor((Math.random()*5)+1));

  function randomizer() {
    console.log("randomized!");
  }

  return (
    <div id="App">
      <div id="header">
        <div id="title">Win's memory game</div>
        <div className="score">Score: {score}</div>

        <button onClick={props.randomizer}>Randomizer</button>
      </div>

    {/* when a pic from tile is clicked, unmount then use component did mount,  
    to change order everytime?*/}

      <Tiles />
    </div>
  );
}

export default App;
