import React, {useState, useEffect} from "react";

const Tiles = () => {

    const [divArray, setDivArray] = useState([
       <div onClick={handleClick} id="1" className="imgWrapper"><img src={require("../imgs/giyu.png")} alt="giyu.png" /><div className="picName">Giyu</div></div>,
       <div onClick={handleClick} id="2" className="imgWrapper"><img src={require("../imgs/gyomei.png")} alt="gyomei.png" /><div className="picName">Gyomei</div></div>,
       <div onClick={handleClick} id="3" className="imgWrapper"><img src={require("../imgs/kanao.png")} alt="kanao.png" /><div className="picName">Kanao</div></div>,
       <div onClick={handleClick} id="4" className="imgWrapper"><img src={require("../imgs/mitsuri.png")} alt="mitsuri.png" /><div className="picName">Mitsuri</div></div>,
       <div onClick={handleClick} id="5" className="imgWrapper"><img src={require("../imgs/muichiro.png")} alt="muichiro.png" /><div className="picName">Muichiro</div></div>,
       <div onClick={handleClick} id="6" className="imgWrapper"><img src={require("../imgs/obanai.png")} alt="obanai.png" /><div className="picName">Obanai</div></div>,
       <div onClick={handleClick} id="7"  className="imgWrapper"><img src={require("../imgs/sakonji.png")} alt="sakonji.png" /><div className="picName">Sakonji</div></div>,
       <div onClick={handleClick} id="8" className="imgWrapper"><img src={require("../imgs/sanemi.png")} alt="sanemi.png" /><div className="picName">Sanemi</div></div>,
       <div onClick={handleClick} id="9" className="imgWrapper"><img src={require("../imgs/shinobu.png")} alt="shinobu.png" /><div className="picName">Shinobu</div></div>,
       <div onClick={handleClick} id="10" className="imgWrapper"><img src={require("../imgs/tanjiro.png")} alt="tanjiro.png" /><div className="picName">Tanjiro</div></div>,
       <div onClick={handleClick} id="11" className="imgWrapper"><img src={require("../imgs/tengen.png")} alt="tengen.png" /><div className="picName">Tengen</div></div>,
       <div onClick={handleClick} id="12" className="imgWrapper"><img src={require("../imgs/zenitsu.png")} alt="zenitsu.png" /><div className="picName">Zenitsu</div></div>,
    ]);

    let [score, setScore] = useState(0);

    const [scoreArray, setScoreArray] = useState([]);        


    function handleClick(e) {

        setScoreArray(scoreArray.push(e.currentTarget.getAttribute("id")));

        let result = false;

        result = scoreArray.some((items, index) => {
            return scoreArray.indexOf(items) !== index
        });

        if (result == true) {
            alert('Duplicate exists')
            setScoreArray([scoreArray.splice(0, scoreArray.length)]);
            setScore(scoreArray.length)
        } else{
            console.log('no duplicates so far...')
            setScore(scoreArray.length)
        }

        console.log(scoreArray)



    };



    function randomizer() {
        console.log('clicked!');

        let copy = [...divArray];
        copy.sort(()=> 0.5 - Math.random());

        setDivArray(copy)

        console.log(copy);

    };

    let i = 1;

    // console.log(Object.keys(divArray))

    return (
        <div>
            <ScoreDiv scoreVal={score}  />
            <div id="tilesWrapper">

                <div><button type="button" onClick={randomizer}>Randomizer</button></div>
                <div id="tileContainer">

                {/* {divArray.map((element, index) => (
                    <div onClick={event => handleClick(event,index)} id={i++} key={index}>{element}</div>
                ))} */}
                {divArray.map((element, index) => 
                <div>{element} key={index}</div>
                )}

                </div>
            </div>
        </div>
    )
};



const ScoreDiv = (props) => {
    return (
        <div className="score">Score: {props.scoreVal}</div>
    )
}


export {Tiles, ScoreDiv};

