import React, {useState, useEffect} from "react";

const Tiles = () => {

    const [array, setArray] = useState([
        <div key={0} onClick={handleClick} className="imgWrapper"><img src={require("../imgs/giyu.png")} alt="giyu.png" /><div className="picName">Giyu</div></div>,
        <div key={1} className="imgWrapper"><img src={require("../imgs/gyomei.png")} alt="gyomei.png" /><div className="picName">Gyomei</div></div>,
        <div key={2} className="imgWrapper"><img src={require("../imgs/kanao.png")} alt="kanao.png" /><div className="picName">Kanao</div></div>,
        <div key={3} className="imgWrapper"><img src={require("../imgs/mitsuri.png")} alt="mitsuri.png" /><div className="picName">Mitsuri</div></div>,
        <div key={4} className="imgWrapper"><img src={require("../imgs/muichiro.png")} alt="muichiro.png" /><div className="picName">Muichiro</div></div>,
        <div key={5} className="imgWrapper"><img src={require("../imgs/obanai.png")} alt="obanai.png" /><div className="picName">Obanai</div></div>,
        <div key={6} className="imgWrapper"><img src={require("../imgs/sakonji.png")} alt="sakonji.png" /><div className="picName">Sakonji</div></div>,
        <div key={7} className="imgWrapper"><img src={require("../imgs/sanemi.png")} alt="sanemi.png" /><div className="picName">Sanemi</div></div>,
        <div key={8} className="imgWrapper"><img src={require("../imgs/shinobu.png")} alt="shinobu.png" /><div className="picName">Shinobu</div></div>,
        <div key={9} className="imgWrapper"><img src={require("../imgs/tanjiro.png")} alt="tanjiro.png" /><div className="picName">Tanjiro</div></div>,
        <div key={10} className="imgWrapper"><img src={require("../imgs/tengen.png")} alt="tengen.png" /><div className="picName">Tengen</div></div>,
        <div key={11} className="imgWrapper"><img src={require("../imgs/zenitsu.png")} alt="zenitsu.png" /><div className="picName">Zenitsu</div></div>,
    ])

    const [scoreArray, setScoreArray] = useState([]);        


    function handleClick(e) {
        console.log(e.currentTarget);

        let addedItems = [];

        // let addedItems3 = addedItems.concat(addedItems2);

        addedItems.concat(e.currentTarget);

        // let addedItems =  scoreArray.concat(e.currentTarget);

        // setScoreArray(scoreArray.push(e.currentTarget))


        console.log(addedItems)
    }



    function randomizer() {
        console.log('clicked!');

        let copy = [...array];
        copy.sort(()=> 0.5 - Math.random());

        setArray(copy)

        // console.log(copy);
    };


    return (
        <div id="tilesWrapper">
            <div><button type="button" onClick={randomizer}>Randomizer</button></div>
            <div id="tileContainer">
            {array}
            </div>
        </div>
    )
};


export default Tiles;

